import React from "react";
import { useEffect, useState } from "react";
const api_base = "http://localhost:3000";

export default function Todo() {
   const [todos, setTodos] = useState([]);
   const [newTodo, setNewTodo] = useState("");

   // Program Functionality
   // Only call this when app loads
   useEffect(() => {
      GetTodos();
      console.log(todos);
   }, []);

   const GetTodos = () => {
      fetch(api_base + "/todo")
         .then((res) => res.json())
         .then((data) => setTodos(data))
         .catch((err) => console.error("Error: ", err));
   };

   const completeTodo = async (id) => {
      const data = await fetch(api_base + "/todo/complete/" + id).then((res) => res.json());

      setTodos((todos) =>
         todos.map((todo) => {
            if (todo._id === data._id) {
               todo.complete = data.complete;
            }
            return todo;
         })
      );
   };

   const addTodo = async () => {
      console.log("Ran!");
      const data = await fetch(api_base + "/todo/new", {
         method: "POST",
         headers: {
            "Content-Type": "application/json",
         },
         body: JSON.stringify({
            text: newTodo,
         }),
      }).then((res) => res.json());

      setTodos([...todos, data]);
      setNewTodo("");
   };

   const deleteTodo = async (id) => {
      const data = await fetch(api_base + "/todo/delete/" + id, { method: "DELETE" }).then((res) => res.json());

      setTodos((todos) => todos.filter((todo) => todo._id !== data.result._id));
   };

   return (
      <div className="App">
         <h3 className="title">Get Stuff Done</h3>

         <form className="submission-form" onSubmit={addTodo}>
            <input
               className="submission-input"
               name="task"
               type="text"
               onChange={(e) => setNewTodo(e.target.value)}
               value={newTodo}
               autoComplete="off"
               required
            />
            <label className="submission-label">
               <span className="task-label">Add Task</span>
            </label>
            <button className="submission-button" type="submit">
               Submit
            </button>
         </form>

         <div className="todo-list">
            {todos.map((todo) => (
               <div className="todo-item" key={todo._id} onClick={() => completeTodo(todo._id)}>
                  <input className="checkbox" type="checkbox" onClick={deleteTodo} />
                  <div className="todo-item">{todo.text}</div>
               </div>
            ))}
         </div>
      </div>
   );
}
